function initWishlistLink() {
    var message;
    jQuery('.link-wishlist').click(function(e){
        e.stopPropagation();
        e.preventDefault();
        var self = jQuery(e.currentTarget);
        jQuery.loadingbar({
            dataType: 'json',
            url : jQuery(e.currentTarget).attr('href'),
            success: function(data) {
                if (data.message) {
                    message = jQuery('<ul class="messages"><li class="success-msg">'+
                            '<ul><li><span>'+data.message+'</span></li></ul></li></ul>');
                    jQuery('.info-messages').before(message);
                    
                    // update the page
                    var href = self.attr('href')+'';
                    if (data.status=='added') {
                        self.closest('.wishing-li').addClass('isWished');
                        href = href.replace("add","remove");
                        self.text('Remove from Wishlist');
                    } else {
                        self.closest('.wishing-li').removeClass('isWished');
                        href = href.replace("remove","add");
                        self.text('Add to Wishlist');
                    }
                    
                    // update the url
                    self.attr('href',href);
                    
                    setTimeout(function(){jQuery('.messages').fadeOut('slow');},2000);
                    return;
                }
                if (data.error) {
                    message = jQuery('<ul class="messages"><li class="error-msg">'+
                            '<ul><li><span>'+data.error+'</span></li></ul></li></ul>');
                    jQuery('.info-messages').before(message);
                    setTimeout(function(){jQuery('.messages').fadeOut('slow');},6000);
                    return;
                }
            },
            error: function(data) {
                window.location = window.location.protocol + '//' + window.location.host + 
                    Mage.Cookies.path + '/customer/account/login/';
                return;
            }
        });
        return false;
    });
}
