function school_grade_level() {
    jQuery('.field-school_grade_level_other').hide();
    $s = jQuery('#school_grade_level');
    if (!$s.length) return;
    if ($s.val()) {
        var text = jQuery("#school_grade_level option[value='"+ $s.val() +"']").text();
        if (text == 'Other') {
            jQuery('.field-school_grade_level_other').show();
        }
    }
}
function school_type() {
    jQuery('.field-school_type_other').hide();
    $s = jQuery('#school_type');
    if (!$s.length) return;
    
    if ($s.val()) {
        var text = jQuery("#school_type option[value='"+ $s.val() +"']").text();
        if (text == 'Other') {
            jQuery('.field-school_type_other').show();
        }
    }
}
function setCustomerType() {
    var $el = jQuery('#select_customer_type');
    if ($el.length) {
        var val = jQuery("#select_customer_type option[value='"+ $el.val() +"']").text().toLowerCase();
    } else {
        var val = jQuery('#select_customer_type-hidden').data('va');
    }
    if (val=='government institution') val = 'government';
    if (val=='non-profit') val = 'nonprofit';
    
    //hide everything else
    if (val!='business')jQuery('.attribute_business').hide();
    if (val!='government')jQuery('.attribute_government').hide();
    if (val!='nonprofit')jQuery('.attribute_nonprofit').hide();
    if (val!='other')jQuery('.attribute_other').hide();
    if (val!='individual')jQuery('.attribute_individual').hide();
    if (val!='school')jQuery('.attribute_school').hide();

    // display the selected type
    jQuery('.attribute_'+ val).show();
    
    jQuery('.field-school_grade_level_other').hide();
    jQuery('.field-school_type_other').hide();
}
// ajax vote init
function initAjaxVote() {
	var disabledClass = 'disabled';
	jQuery('.votes-block[data-url]').each(function() {
		var link = jQuery(this);
		var resultTarget = link.find('.info');
		link.click(function(e) {
			e.preventDefault();
			if (!link.hasClass(disabledClass)) {
				link.addClass(disabledClass);
				jQuery.loadingbar({
					type: 'get',
					url: link.attr('data-url'),
					data: 'ajax=1',
					success: function(data) {
						resultTarget.text(data);
					}
				});
			}
		});
	});
}
function initAjaxCart() {
	var messageHolder = jQuery('.info-messages');
	var message = messageHolder.find('.ajax-message');
	var cart = jQuery('.header-minicart .count');
	var cartBlock = jQuery('#header-cart');
	var ajaxBusy = false;
	var body = jQuery('body');
	var loadingClass = 'loading';

	jQuery('.link-add.ajax-cart').each(function() {
		var link = jQuery(this);
		var productName = link.closest('.product-info').find('.product-name').text();

		link.click(function(e) {
			e.preventDefault();
			if (!ajaxBusy) {
				sendAjax(link.attr('href'), productName);
			}
		});
	});
	
	jQuery('#product_addtocart_form').each(function() {
		var form = jQuery(this);
		var btn = form.find('.btn-cart');
		var productName = form.find('.page-title h1').eq(0).text();
		btn.removeAttr('onclick');

		btn.click(function(e) {
			e.preventDefault();
			if (!ajaxBusy) {
				body.addClass(loadingClass);
				jQuery.loadingbar({
					type: form.attr('method'),
					url: form.attr('action'),
					data: form.serialize(),
					success: function(data) {
                                            console.log('success 533', data);
                                            try {
                                                data = jQuery.parseJSON(data);
                                                if (data.error) {
                                                    var error = jQuery('<ul class="messages"><li class="error-msg"><ul><li><span>'+
                                                            data.error+'</span></li></ul></li></ul>');
                                                    jQuery('.product-options-bottom').before(error);
                                                    setTimeout(function(){error.fadeOut('slow');},6000);
                                                    return;
                                                } else if (data.redirect) {
                                                    window.location = data.redirect;
                                                    return;
                                                }
                                            } catch (e) {}
//                                            if (jQuery('.checkout-cart-configure').length) {
//                                                var _url = window.location.protocol + '//' + window.location.host + 
//                                                            Mage.Cookies.path + '/checkout/cart';
//                                                window.location = _url;
//                                            }
					    cahngeCartInfo(productName);
					},
                                        error :function(data) {
                                            console.log('error 553', data);
                                        }
				});
			}
		});
	});

	function sendAjax(url, productName) {
		ajaxBusy = true;
		body.addClass(loadingClass);
		jQuery.loadingbar({
			type: 'post',
			url: url,
			success: function(data) {
                                            console.log('success 568', data);
                            cahngeCartInfo(productName);
			},
                        error :function(data) {
                            console.log('error 569', data);
                        }
		});
	}
	function cahngeCartInfo(productName) {
		jQuery.loadingbar({
			type: 'get',
			url: window.location.protocol + '//' + window.location.host + 
                                Mage.Cookies.path + '/ajax-cart',
			data: 'ajax=1',
			success: function(data) {
                            console.log('success', data);
				var newContent = jQuery('<div>', {
					html: data
				});
				var newText = newContent.find('.header-minicart .count').text();

				cart.text(newText);
				cartBlock.html(newContent.find('#header-cart').html());

				showMessage(productName);
				body.removeClass(loadingClass);
				ajaxBusy = false;
                                
                                if (productName) {
                                    showMessage(productName);
                                }
                                body.removeClass(loadingClass);                                
                                var Mini = new Minicart({
                                    formKey:FORM_KEY
                                });
                                Mini.init();
			},
                        error :function(data) {
                            console.log('error 599', data);
                        }
		});
	}
        var old;
	function showMessage(text) {
		message.text('"' + text + '" was successfully added to your cart.');
                var w = jQuery('body').width();
                messageHolder.width(w);
		messageHolder.show();
                clearTimeout(old);
                old = setTimeout(hideMessage, 6000);
	}
        function hideMessage() {
            messageHolder.fadeOut('slow');
        }
}

// banner fix
function fixBanner() {
	var win = jQuery(window);
	jQuery('#divSimpleSlider').each(function() {
		var banner = jQuery(this);
		var slides = banner.find('>.banner');

		refreshBannerHeight();
		win.bind('resize orientationchange', function() {
			refreshBannerHeight();
		});
		function refreshBannerHeight() {
			var maxHeight = Math.floor(getMaxHeight(slides));
			if (Math.abs(banner.height() - maxHeight) > 1) {
				banner.css({height: maxHeight});
			}
		}
	});

	function getMaxHeight(items) {
		var max = 0;
		items.each(function() {
			var item = jQuery(this);
			if (item.outerHeight() > max) max = item.outerHeight();
		});
		return max;
	}
}

